mapper.insert:
		INSERT INTO WC (WC_NO,
						USER_ID,
						AGE,
						GENDER,
						TAG_WORD,
						TITLE,
						MBTI)
				 VALUES(
            		 WC_SEQ.NEXTVAL
            	   ,#{userId}
            	   ,#{age}
            	   ,#{gender}
            	   ,#{tagWords}
            	   ,#{title}
            	   ,#{MBTI}
            	 )

mapper.select:
SELECT COUNT(*)
FROM WC
WHERE TITLE=#{title}
