mapper.insert:
INSERT ALL

	INTO TRIP(CONTENT_ID
			 ,CONTENT_TYPE_ID
			 ,TITLE
			 ,ADDR1
			 ,ADDR2
			 ,ZIP_CODE
			 ,AREA_CODE
			 ,FIRST_IMAGE
			 ,FIRST_IMAGE2)
	VALUES(#{Trip.contentId}
		  ,#{Trip.contentTypeId}
		  ,#{Trip.title}
		  ,#{Trip.addr1}
		  ,#{Trip.addr2}
		  ,#{Trip.zipCode}
		  ,#{Trip.areaCode}
		  ,#{Trip.firstImage1}
		  ,#{Trip.firstImage2})

SELECT * FROM DUAL

mapper.select:
SELECT COUNT(*) 
FROM TRIP 
WHERE FIRST_IMAGE2 IS NOT NULL 
AND CONTENT_TYPE_ID=#{contentTypeId}
AND AREA_CODE = #{areaCode}

mapper.update:
UPDATE TRIP 
SET COUNT = COUNT +1
WHERE CONTENT_ID = #{contentId}

mapper.select:
SELECT COUNT
FROM TRIP
WHERE CONTENT_ID = #{contentId}

SELECT LIKE_COUNT
FROM TRIP
WHERE CONTENT_ID = #{contentId}

SELECT USER_ID
FROM LIKES 
WHERE USER_ID= #{userId} 
AND CONTENT_ID= #{contentId}

mapper.update:
UPDATE TRIP 
SET LIKE_COUNT = LIKE_COUNT+1
WHERE CONTENT_ID = #{contentId}

mapper.delete:
DELETE FROM LIKES 
WHERE USER_ID= #{userId} 
AND CONTENT_ID= #{contentId}

mapper.update:
UPDATE TRIP 
SET LIKE_COUNT = LIKE_COUNT-1
WHERE CONTENT_ID = #{contentId}

UPDATE TRIP_REPLY 
SET REPLY_CONTENT = #{replyContent} 
WHERE REPLY_NO = #{replyNo}

mapper.select:
SELECT CONTENT_ID ,CONTENT_TYPE_ID ,TITLE ,ADDR1 ,ADDR2 ,ZIP_CODE ,AREA_CODE ,FIRST_IMAGE2 ,COUNT FROM TRIP WHERE FIRST_IMAGE2 IS NOT NULL
AND CONTENT_TYPE_ID=#{id}
AND TITLE LIKE '%'||#{keyword}||'%' ORDER BY COUNT DESC

SELECT COUNT(*) FROM TRIP WHERE FIRST_IMAGE2 IS NOT NULL
AND CONTENT_TYPE_ID=#{id}
AND TITLE LIKE '%'||#{keyword}||'%'
